    def _client(self):
        _client = ModbusSerialClient(method = str(self.type.currentText()),
        port= str(self.Port.currentText()),
        stopbits = int(self.stop_bit.currentText()), 
        bytesize = int(self.byte_size.currentText()),
        parity = str(self.parity.currentText()), baudrate=int(self.baudrate.currentText()))
        return _client
    def Connection_server(self):
        
        client = self._client()
        # global data_1 = client
        # print(type(client))
        result_2 = client.read_holding_registers(address=40000,count=8, unit=1)
        # print('Slave 1:' +str(result_2.registers) )
        # print(type(result_2.registers))
        if(client.connect() == True):
            self.Connect.setText("Connected")
            self.Disconnect.setText('DisConnect')
        elif (client.connect()== False):
            self.Connect.setText("Try again")
            self.Disconnect.setText('DisConnect')
        self.data_1.setText(str(result_2.registers[0]))
        self.data_2.setText(str(result_2.registers[1]))
        self.data_3.setText(str(result_2.registers[2]))
        self.data_4.setText(str(result_2.registers[3]))
        self.data_5.setText(str(result_2.registers[4]))
        self.data_6.setText(str(result_2.registers[5]))
        self.data_7.setText(str(result_2.registers[6]))
        self.data_8.setText(str(result_2.registers[7]))
        self.Slave_1.setValue(1)
        self.Slave_2.setValue(2)
        client.close()
                
